import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';

function Chart({ gap, data, options = {} }) {
  const chartContainerRef = useRef();

  useEffect(() => {
    console.log('Rendering Chart with data:', data);
    if (!gap || !data || data.length === 0) return;

    const {
      width = 600,
      height = 400,
      showVolume = false,
      showVWAP = false,
      showTransactions = false,
      focusOnGap = true,
      timeFormat = 'milliseconds',
    } = options;

    const chart = createChart(chartContainerRef.current, {
      width,
      height,
      layout: {
        background: { color: '#2c3e50' },
        textColor: '#ecf0f1',
      },
      grid: {
        vertLines: { color: 'rgba(236, 240, 241, 0.1)' },
        horzLines: { color: 'rgba(236, 240, 241, 0.1)' },
      },
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
        borderColor: '#ecf0f1',
      },
      rightPriceScale: {
        borderColor: '#ecf0f1',
      },
      crosshair: {
        vertLine: {
          color: 'rgba(236, 240, 241, 0.5)',
          width: 1,
          style: 1,
        },
        horzLine: {
          color: 'rgba(236, 240, 241, 0.5)',
          width: 1,
          style: 1,
        },
      },
    });

    const candlestickSeries = chart.addCandlestickSeries({
      upColor: '#2ecc71',
      downColor: '#e74c3c',
      borderVisible: false,
      wickUpColor: '#2ecc71',
      wickDownColor: '#e74c3c',
    });

    const formattedData = data.map(item => ({
      time: timeFormat === 'milliseconds' ? item.t / 1000 : item.date,
      open: parseFloat(item.o || item.open) || 0,
      high: parseFloat(item.h || item.high) || 0,
      low: parseFloat(item.l || item.low) || 0,
      close: parseFloat(item.c || item.close) || 0,
      volume: item.v || 0,
      vwap: item.vw || 0,
      transactions: item.n || 0,
    }));

    candlestickSeries.setData(formattedData);

    if (showVolume) {
      const volumeSeries = chart.addHistogramSeries({
        color: '#26a69a',
        priceFormat: { type: 'volume' },
        priceScaleId: 'volume',
        scaleMargins: { top: 0.8, bottom: 0 },
      });
      volumeSeries.setData(formattedData.map(item => ({
        time: item.time,
        value: item.volume,
        color: item.close > item.open ? 'rgba(38, 166, 154, 0.5)' : 'rgba(239, 83, 80, 0.5)',
      })));
    }

    if (showVWAP) {
      const vwapSeries = chart.addLineSeries({
        color: 'rgba(255, 192, 0, 0.8)',
        lineWidth: 2,
        priceScaleId: 'right',
      });
      vwapSeries.setData(formattedData.map(item => ({
        time: item.time,
        value: item.vwap,
      })));
    }

    if (showTransactions) {
      const transactionsSeries = chart.addLineSeries({
        color: 'rgba(76, 175, 80, 0.8)',
        lineWidth: 2,
        priceScaleId: 'left',
      });
      transactionsSeries.setData(formattedData.map(item => ({
        time: item.time,
        value: item.transactions,
      })));
    }

    // Add a marker for the gap
    const gapDate = gap.date; // Use the gap date directly
    const gapIndex = formattedData.findIndex(item => {
      const itemDate = new Date(item.time * 1000).toISOString().split('T')[0];
      return itemDate === gapDate;
    });

    if (gapIndex !== -1) {
      const markerTime = gapDate;
      candlestickSeries.setMarkers([
        {
          time: markerTime,
          position: 'aboveBar',
          color: 'black',
          shape: 'arrowDown',
          text: 'Gap',
        },
      ]);

      if (focusOnGap) {
        const rangeStart = Math.max(0, gapIndex - 10);
        const rangeEnd = Math.min(formattedData.length - 1, gapIndex + 10);

        chart.timeScale().setVisibleRange({
          from: formattedData[rangeStart].time,
          to: formattedData[rangeEnd].time,
        });
      } else {
        chart.timeScale().fitContent();
      }
    } else {
      console.warn('Gap date not found in the data');
      chart.timeScale().fitContent();
    }

    return () => {
      chart.remove();
    };
  }, [gap, data, options]);

  return <div ref={chartContainerRef} className="ChartContainer" />;
}

export default Chart;
