import React, { useEffect, useRef, useState } from 'react';
import { createChart } from 'lightweight-charts';
import { DateTime } from 'luxon';

function IntradayChart({ gap, data }) {
  const chartContainerRef = useRef();
  const [chartSize, setChartSize] = useState({ width: 0, height: 400 });

  // Debug: Log props
  useEffect(() => {
    console.log('IntradayChart Props:', { gap, data });
  }, [gap, data]);

  useEffect(() => {
    const updateSize = () => {
      if (chartContainerRef.current) {
        setChartSize({
          width: chartContainerRef.current.clientWidth,
          height: chartContainerRef.current.clientHeight
        });
      }
    };

    window.addEventListener('resize', updateSize);
    updateSize();

    return () => window.removeEventListener('resize', updateSize);
  }, []);

  useEffect(() => {
    console.log('Rendering IntradayChart with data:', data);
    if (!gap || !data || data.length === 0 || chartSize.width === 0) {
      console.warn('IntradayChart: Missing or empty data.');
      return;
    }

    const chart = createChart(chartContainerRef.current, {
      width: chartSize.width,
      height: chartSize.height,
      layout: {
        background: { color: '#ffffff' },
        textColor: '#333',
      },
      grid: {
        vertLines: { color: '#f0f0f0' },
        horzLines: { color: '#f0f0f0' },
      },
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
        tickMarkFormatter: (time) => {
          const date = DateTime.fromSeconds(time).setZone('America/Los_Angeles');
          return date.toFormat('HH:mm');
        },
      },
      rightPriceScale: {
        scaleMargins: {
          top: 0.1,
          bottom: 0.25, // Adjusted for better spacing
        },
      },
    });

    const lineSeries = chart.addLineSeries({
      color: '#2196F3',
      lineWidth: 2,
    });

    // Ensure data is in the correct format
    const formattedData = data.map(item => {
      if (!item || typeof item.time === 'undefined') {
        console.warn('Invalid data item:', item);
        return null;
      }

      let timestamp;
      if (typeof item.time === 'string') {
        timestamp = DateTime.fromISO(item.time).toSeconds();
      } else if (typeof item.time === 'number') {
        timestamp = item.time;
      } else {
        console.warn('Invalid time format:', item.time);
        return null;
      }

      return { 
        time: timestamp, 
        value: item.close || item.value // Adjust based on your data structure
      };
    }).filter(Boolean);

    console.log('Formatted data (first 5 items):', formattedData.slice(0, 5));

    if (formattedData.length > 0) {
      lineSeries.setData(formattedData);
      chart.timeScale().fitContent();
    } else {
      console.warn('No valid data points after formatting');
    }

    return () => {
      chart.remove();
    };
  }, [gap, data, chartSize]);

  return <div ref={chartContainerRef} className="ChartContainer" style={{ width: '100%', height: '100%' }} />;
}

export default IntradayChart;
