import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';
import { DateTime } from 'luxon';

function IntradayChart({ gap, data }) {
  const chartContainerRef = useRef();

  useEffect(() => {
    console.log('Rendering IntradayChart with data:', data);
    if (!gap || !data || data.length === 0) return;

    const chart = createChart(chartContainerRef.current, {
      width: 600,
      height: 400,
      layout: {
        background: { color: '#ffffff' },
        textColor: '#333',
      },
      grid: {
        vertLines: { color: '#f0f0f0' },
        horzLines: { color: '#f0f0f0' },
      },
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
        tickMarkFormatter: (time) => {
          const date = DateTime.fromSeconds(time).setZone('America/Los_Angeles');
          return date.toFormat('HH:mm');
        },
      },
    });

    const candlestickSeries = chart.addCandlestickSeries();

    // Add these new series for market hour indicators
    const preMarketLine = chart.addLineSeries({
      color: 'rgba(100, 100, 100, 0.8)',
      lineWidth: 1,
      lineStyle: 2, // Dashed line
      priceScaleId: '', // Make it price-scale independent
    });

    const marketOpenLine = chart.addLineSeries({
      color: 'rgba(0, 128, 0, 0.8)', // Green color
      lineWidth: 2,
      priceScaleId: '',
    });

    const marketCloseLine = chart.addLineSeries({
      color: 'rgba(255, 0, 0, 0.8)', // Red color
      lineWidth: 2,
      priceScaleId: '',
    });

    const afterHoursLine = chart.addLineSeries({
      color: 'rgba(100, 100, 100, 0.8)',
      lineWidth: 1,
      lineStyle: 2, // Dashed line
      priceScaleId: '',
    });

    // VWAP series (previously VWAP2)
    const vwapSeries = chart.addLineSeries({
      color: 'rgba(128, 0, 128, 0.8)', // Purple color
      lineWidth: 2,
      priceScaleId: 'right',
    });

    // Add EMA series
    const emaSeries = chart.addLineSeries({
      color: 'rgba(255, 82, 82, 0.8)',
      lineWidth: 2,
      priceScaleId: 'right',
    });

    const formattedData = data.map(item => ({
      time: DateTime.fromMillis(item.timestamp).setZone('America/Los_Angeles').toSeconds(),
      open: item.open,
      high: item.high,
      low: item.low,
      close: item.close,
      volume: item.volume,
    }));

    // Generate market hours indicator data
    const { preMarketLineData, marketOpenLineData, marketCloseLineData, afterHoursLineData } = generateMarketHoursIndicators(formattedData);

    // Calculate VWAP
    const vwapData = calculateVWAP(formattedData);

    console.log('VWAP Data:', vwapData);

    // Calculate 10-period EMA
    const emaData = calculateEMA(formattedData, 10);

    candlestickSeries.setData(formattedData);
    preMarketLine.setData(preMarketLineData);
    marketOpenLine.setData(marketOpenLineData);
    marketCloseLine.setData(marketCloseLineData);
    afterHoursLine.setData(afterHoursLineData);
    vwapSeries.setData(vwapData);
    emaSeries.setData(emaData);

    chart.timeScale().fitContent();

    const handleResize = () => {
      chart.applyOptions({ width: chartContainerRef.current.clientWidth });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      chart.remove();
    };
  }, [gap, data]);

  // Add this function outside the useEffect hook
  const calculateVWAP = (data) => {
    let cumulativePV = 0;
    let cumulativeVolume = 0;

    return data.map((item) => {
      const typicalPrice = (item.high + item.low + item.close) / 3;
      cumulativePV += typicalPrice * item.volume;
      cumulativeVolume += item.volume;
      const vwap = cumulativePV / cumulativeVolume;

      return {
        time: item.time,
        value: vwap
      };
    });
  };

  // Add this function outside the useEffect hook
  const calculateEMA = (data, period) => {
    const k = 2 / (period + 1);
    let ema = data[0].close;

    return data.map((item, index) => {
      if (index === 0) {
        return { time: item.time, value: ema };
      }
      ema = (item.close - ema) * k + ema;
      return { time: item.time, value: ema };
    });
  };

  // Replace the generateMarketHoursData function with this new one
  const generateMarketHoursIndicators = (data) => {
    const preMarketLineData = [];
    const marketOpenLineData = [];
    const marketCloseLineData = [];
    const afterHoursLineData = [];

    data.forEach((item) => {
      const date = DateTime.fromSeconds(item.time).setZone('America/Los_Angeles');
      const hour = date.hour;
      const minute = date.minute;

      if (hour === 4) {
        preMarketLineData.push({ time: item.time, value: 0 });
      } else if (hour === 6 && minute === 30) {
        marketOpenLineData.push({ time: item.time, value: 0 });
      } else if (hour === 13) {
        marketCloseLineData.push({ time: item.time, value: 0 });
      } else if (hour === 20) {
        afterHoursLineData.push({ time: item.time, value: 0 });
      }
    });

    return { preMarketLineData, marketOpenLineData, marketCloseLineData, afterHoursLineData };
  };

  return <div ref={chartContainerRef} className="ChartContainer" />;
}

export default IntradayChart;
