import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';

function IntradayChart({ gap, intradayData }) {
  const chartContainerRef = useRef();

  useEffect(() => {
    console.log('Rendering IntradayChart with data:', gap.intraday_data); // **Debug Log**

    if (!gap || !intradayData || intradayData.length === 0) return;

    const chart = createChart(chartContainerRef.current, {
      width: 600,
      height: 300,
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
      },
      layout: {
        textColor: '#d1d4dc',
        background: { type: 'solid', color: '#000000' },
      },
    });

    chart.applyOptions({
      rightPriceScale: {
        scaleMargins: {
          top: 0.3, // leave some space for the legend
          bottom: 0.25,
        },
      },
    });

    const candlestickSeries = chart.addCandlestickSeries();

    const formattedData = intradayData.map(item => ({
      time: Math.floor(new Date(item.timestamp).getTime() / 1000), // Convert to UNIX timestamp in seconds
      open: item.open,
      high: item.high,
      low: item.low,
      close: item.close,
    }));

    candlestickSeries.setData(formattedData);

    const legend = document.createElement('div');
    legend.style = `position: absolute; left: 12px; top: 12px; z-index: 1; font-size: 14px; font-family: sans-serif; line-height: 18px; font-weight: 300;`;
    legend.style.color = 'white';
    legend.textContent = 'Intraday Chart';
    chartContainerRef.current.appendChild(legend);

    chart.timeScale().fitContent();

    return () => {
      chart.remove();
      if (chartContainerRef.current) {
        chartContainerRef.current.removeChild(legend);
      }
    };
  }, [gap, intradayData]);

  return <div ref={chartContainerRef} />;
}

export default IntradayChart;
