const API_BASE_URL = 'http://localhost:5000/api';

export async function fetchDailyData(ticker, startDate, endDate) {
  const response = await fetch(`${API_BASE_URL}/daily_data?ticker=${ticker}&start_date=${startDate}&end_date=${endDate}`);
  if (!response.ok) {
    throw new Error('Failed to fetch daily data');
  }
  return response.json();
}

export async function fetchIntradayData(ticker, date) {
  const response = await fetch(`${API_BASE_URL}/intraday_data?ticker=${ticker}&date=${date}`);
  if (!response.ok) {
    throw new Error('Failed to fetch intraday data');
  }
  return response.json();
}
export async function fetchGapData(searchParams) {
  const queryString = new URLSearchParams(searchParams).toString();
  const response = await fetch(`${API_BASE_URL}/gap_stats?${queryString}`);
  if (!response.ok) {
    throw new Error('Failed to fetch gap data');
  }
  return response.json();
}
