import React, { useState } from 'react';
import SearchForm from './components/SearchForm';
import GapList from './components/GapList';
import Chart from './components/Chart';
import IntradayChart from './components/IntradayChart';
import './App.css';
import { fetchDailyData, fetchGapData } from './api';

function App() {
  const [gapData, setGapData] = useState([]);
  const [selectedGap, setSelectedGap] = useState(null);
  const [dailyData, setDailyData] = useState([]);
  const [intradayData, setIntradayData] = useState([]); // **Added State Variable**

  const handleSearch = async (searchParams) => {
    console.log('Searching with params:', searchParams);
    try {
      const data = await fetchGapData(searchParams);
      console.log('Received data:', data);
      setGapData(data);
    } catch (error) {
      console.error('Error fetching gap data:', error);
      // Handle error (e.g., set error state)
    }
  };

  const handleGapSelect = async (gap) => {
    console.log('Selected gap:', gap); // **Debug Log**
    setSelectedGap(gap);
    try {
      setDailyData(gap.daily_data);
      setIntradayData(gap.intraday_data);
    } catch (error) {
      console.error('Error fetching chart data:', error);
      // Handle error (e.g., set error state)
    }
  };

  return (
    <div className="App">
      <h1>Small Cap Gaps</h1>
      <SearchForm onSearch={handleSearch} />
      <GapList gaps={gapData} onSelectGap={handleGapSelect} />
      {selectedGap && (
        <>
          {/* **Updated Prop Name from dailyData to data** */}
          <Chart gap={selectedGap} gaps={gapData} />
          <IntradayChart gap={selectedGap} intradayData={intradayData} />
        </>
      )}
    </div>
  );
}

export default App;
