import React, { useState, useEffect } from 'react';
import SearchForm from './components/SearchForm';
import GapList from './components/GapList';
import Chart from './components/Chart';
import IntradayChart from './components/IntradayChart';
import './App.css';
import { fetchGapData, fetchDailyData } from './api';

function App() {
  const [gapData, setGapData] = useState([]);
  const [selectedGap, setSelectedGap] = useState(null);
  const [dailyData, setDailyData] = useState(null);
  const [intradayData, setIntradayData] = useState(null);

  const handleSearch = async (searchParams) => {
    console.log('Searching with params:', searchParams);
    try {
      const data = await fetchGapData(searchParams);
      console.log('Received data:', data);
      // Sort the data by gap_percent in descending order
      const sortedData = data.sort((a, b) => b.gap_percent - a.gap_percent);
      setGapData(sortedData);
      setSelectedGap(null);
      setDailyData(null);
      setIntradayData(null);
    } catch (error) {
      console.error('Error fetching gap data:', error);
      // Handle error (e.g., set error state)
    }
  };

  const handleGapSelect = async (gap) => {
    if (selectedGap && selectedGap.ticker === gap.ticker && selectedGap.date === gap.date) {
      setSelectedGap(null);
      setDailyData(null);
      setIntradayData(null);
    } else {
      setSelectedGap(gap);
      try {
        // Fetch daily data
        const dailyData = await fetchDailyData({
          ticker: gap.ticker,
          start_date: gap.date,
          end_date: gap.date
        });
        setDailyData(dailyData);

        // Fetch intraday data (you'll need to implement this API call)
        // const intradayData = await fetchIntradayData(gap.ticker, gap.date);
        // setIntradayData(intradayData);
      } catch (error) {
        console.error('Error fetching chart data:', error);
        // Handle error (e.g., set error state)
      }
    }
  };

  return (
    <div className="App">
      <h1>Small Cap Gaps</h1>
      <SearchForm onSearch={handleSearch} />
      <GapList gaps={gapData} onSelectGap={handleGapSelect} />
      {selectedGap && dailyData && (
        <div className="charts-container">
          <div className="chart-wrapper">
            <Chart gap={selectedGap} data={dailyData} options={{ width: 600, height: 400 }} />
          </div>
          {intradayData && (
            <div className="chart-wrapper">
              <IntradayChart gap={selectedGap} data={intradayData} />
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
