import React, { useState } from 'react';
import Chart from './Chart';
import IntradayChart from './IntradayChart';

function GapDataWithChart({ gap, onSelectGap }) {
  const [isExpanded, setIsExpanded] = useState(false);

  const formatNumber = (value) => {
    return typeof value === 'number' ? value.toFixed(2) : 'N/A';
  };

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
    if (!isExpanded) {
      onSelectGap(gap);
    }
  };

  return (
    <div className="gap-data-with-chart">
      <div className="gap-data" onClick={toggleExpand}>
        {gap.ticker || 'Unknown'} - {gap.date || 'No date'} - 
        Gap: {formatNumber(gap.gap_percent)}% - 
        Return: {formatNumber(gap.return)}%
        {gap.did_close_red && (
          <span style={{ color: 'red' }}> - Did Close</span>
        )}
      </div>
      {isExpanded && (
        <div className="chart-container">
          <Chart gap={gap} data={gap.dailyData} />
          <IntradayChart gap={gap} data={gap.intradayData} />
        </div>
      )}
    </div>
  );
}

export default GapDataWithChart;
