import React, { useState } from 'react';
import SearchForm from './components/SearchForm';
import GapList from './components/GapList';
import Chart from './components/Chart';
import './App.css';
import { fetchDailyData } from './api';

function App() {
  const [gapData, setGapData] = useState([]);
  const [selectedGap, setSelectedGap] = useState(null);
  const [dailyData, setDailyData] = useState([]);

  const handleSearch = async (searchParams) => {
    console.log('Searching with params:', searchParams);
    const response = await fetch(`http://localhost:5000/api/gap_stats?${new URLSearchParams(searchParams)}`);
    const data = await response.json();
    console.log('Received data:', data);
    setGapData(data);
  };

  const handleGapSelect = async (gap) => {
    setSelectedGap(gap);
    try {
      const daily = await fetchDailyData({
        ticker: gap.ticker,
        start_date: gap.start_chart_date,
        end_date: gap.end_chart_date
      });
      setDailyData(daily);
    } catch (error) {
      console.error('Error fetching chart data:', error);
      // Handle error (e.g., set error state)
    }
  };

  return (
    <div className="App">
      <h1>Small Cap Gaps</h1>
      <SearchForm onSearch={handleSearch} />
      <GapList gaps={gapData} onSelectGap={handleGapSelect} />
      {selectedGap && <Chart gap={selectedGap} dailyData={dailyData} />}
    </div>
  );
}

export default App;
