import React from 'react';

function GapList({ gaps, onSelectGap }) {
  if (!Array.isArray(gaps) || gaps.length === 0) {
    return <p>No gap data available.</p>;
  }

  const formatNumber = (value) => {
    return typeof value === 'number' ? value.toFixed(2) : 'N/A';
  };

  return (
    <ul>
      {gaps.map((gap, index) => (
        <li key={`${gap.ticker || 'unknown'}-${gap.date || index}`} onClick={() => onSelectGap(gap)}>
          {gap.ticker || 'Unknown'} - {gap.date || 'No date'} - 
          Gap: {formatNumber(gap.gap_percent)}% - 
          Return: {formatNumber(gap.return_percent)}%
          {gap.did_close_red && (
            <span style={{ color: 'red' }}> - Did Close</span>
          )}
        </li>
      ))}
    </ul>
  );
}

export default GapList;
