import React, { useState } from 'react';
import SearchForm from './components/SearchForm';
import GapList from './components/GapList';
import Chart from './components/Chart';
import IntradayChart from './components/IntradayChart';
import './App.css';
import { fetchGapData } from './api';

function App() {
  const [gapData, setGapData] = useState([]);
  const [selectedGap, setSelectedGap] = useState(null);

  const handleSearch = async (searchParams) => {
    console.log('Searching with params:', searchParams);
    try {
      const data = await fetchGapData(searchParams);
      console.log('Received data:', data);
      setGapData(data);
    } catch (error) {
      console.error('Error fetching gap data:', error);
      // Handle error (e.g., set error state)
    }
  };

  const handleGapSelect = (gap) => {
    console.log('Selected gap:', gap);
    setSelectedGap(gap);
  };

  return (
    <div className="App">
      <h1>Small Cap Gaps</h1>
      <SearchForm onSearch={handleSearch} />
      <GapList gaps={gapData} onSelectGap={handleGapSelect} />
      {selectedGap &&(
        <>
          <Chart gap={selectedGap} data={selectedGap.daily_data} />
          <IntradayChart gap={selectedGap} data={selectedGap.intraday_data[0]} />
        </>
      )}
    </div>
  );
}

export default App;
