import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';

function IntradayChart({ gap, intradayData }) {
  const chartContainerRef = useRef();

  useEffect(() => {
    if (!gap || !intradayData || intradayData.length === 0) return;

    const chart = createChart(chartContainerRef.current, {
      width: 600,
      height: 300,
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
      },
    });

    const candlestickSeries = chart.addCandlestickSeries();

    const formattedData = intradayData.map(item => ({
      time: new Date(item.datetime).getTime() / 1000,
      open: parseFloat(item.open),
      high: parseFloat(item.high),
      low: parseFloat(item.low),
      close: parseFloat(item.close),
    }));

    candlestickSeries.setData(formattedData);

    candlestickSeries.setMarkers([
      {
        time: new Date(gap.datetime).getTime() / 1000,
        position: 'aboveBar',
        color: 'red',
        shape: 'arrowDown',
        text: 'Gap',
      },
    ]);

    const gapIndex = formattedData.findIndex(item => 
      new Date(item.time * 1000).toISOString() === gap.datetime
    );
    const rangeStart = Math.max(0, gapIndex - 30);
    const rangeEnd = Math.min(formattedData.length - 1, gapIndex + 30);

    chart.timeScale().setVisibleRange({
      from: formattedData[rangeStart].time,
      to: formattedData[rangeEnd].time,
    });

    return () => {
      chart.remove();
    };
  }, [gap, intradayData]);

  return <div ref={chartContainerRef} />;
}

export default IntradayChart;
