import React, { useState } from 'react';
import SearchForm from './components/SearchForm';
import GapList from './components/GapList';
import Chart from './components/Chart';
import IntradayChart from './components/IntradayChart';
import './App.css';
import { fetchGapData } from './api';

function App() {
  const [gapData, setGapData] = useState([]);
  const [selectedGap, setSelectedGap] = useState(null);
  const [expandedGap, setExpandedGap] = useState(null);

  const handleSearch = async (searchParams) => {
    console.log('Searching with params:', searchParams);
    try {
      const data = await fetchGapData(searchParams);
      console.log('Received data:', data);
      setGapData(data);
    } catch (error) {
      console.error('Error fetching gap data:', error);
      // Handle error (e.g., set error state)
    }
  };

  const handleGapToggle = (gap) => {
    if (expandedGap && expandedGap.ticker === gap.ticker && expandedGap.date === gap.date) {
      setExpandedGap(null);
    } else {
      setExpandedGap(gap);
      setSelectedGap(gap);
    }
  };

  return (
    <div className="App">
      <h1>Small Cap Gaps</h1>
      <SearchForm onSearch={handleSearch} />
      {gapData.map((gap, index) => (
        <React.Fragment key={`${gap.ticker}-${gap.date}`}>
          <GapList gaps={[gap]} onSelectGap={handleGapToggle} />
          {expandedGap && expandedGap.ticker === gap.ticker && expandedGap.date === gap.date && (
            <div className="charts-container">
              <div className="chart-wrapper">
                <Chart gap={expandedGap} data={expandedGap.daily_data} options={{ width: 600, height: 400 }} />
              </div>
              <div className="chart-wrapper">
                <IntradayChart gap={expandedGap} data={expandedGap.intraday_data} />
              </div>
            </div>
          )}
        </React.Fragment>
      ))}
    </div>
  );
}

export default App;
