import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';

function Chart({ gap, dailyData }) {
  const chartContainerRef = useRef();

  useEffect(() => {
    console.log('Rendering Chart with data:', dailyData); // **Debug Log**

    if (!gap || !dailyData || dailyData.length === 0) return;

    const chart = createChart(chartContainerRef.current, {
      width: 600,
      height: 300,
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
      },
    });

    const candlestickSeries = chart.addCandlestickSeries();

    const formattedData = dailyData.map(item => ({
      time: item.date, // Ensure 'date' is in 'YYYY-MM-DD' format
      open: item.open,
      high: item.high,
      low: item.low,
      close: item.close,
    }));

    candlestickSeries.setData(formattedData);

    // Add a marker for the gap
    const gapTime = gap.date; // 'gap.date' should correspond to a daily date format
    candlestickSeries.setMarkers([
      {
        time: gapTime,
        position: 'aboveBar',
        color: 'blue', // Different color to distinguish from IntradayChart
        shape: 'arrowUp',
        text: 'Gap',
      },
    ]);

    chart.timeScale().fitContent();

    return () => {
      chart.remove();
    };
  }, [gap, dailyData]);

  return <div ref={chartContainerRef} />;
}

export default Chart;
