import React from 'react';

function GapList({ gaps, onSelectGap }) {
  if (!Array.isArray(gaps) || gaps.length === 0) {
    return <p>No gap data available.</p>;
  }

  return (
    <ul className="gap-list">
      {gaps.map((gap, index) => (
        <li 
          key={`${gap.ticker || 'unknown'}-${gap.date || index}`} 
          onClick={() => onSelectGap(gap)}
          className="gap-item"
        >
          <div className="gap-content">
            <span className="gap-ticker">{gap.ticker || 'Unknown'}</span>
            <span className="gap-date">{gap.date || 'No date'}</span>
            {gap.did_close_red && (
              <span className="gap-close-red">Did Close</span>
            )}
          </div>
        </li>
      ))}
    </ul>
  );
}

export default GapList;
