import React, { useEffect, useRef, useState } from 'react';
import { createChart } from 'lightweight-charts';
import { DateTime } from 'luxon';

function IntradayChart({ gap, data }) {
  const chartContainerRef = useRef();
  const [chartSize, setChartSize] = useState({ width: 0, height: 500 }); // Increased height

  useEffect(() => {
    const updateSize = () => {
      if (chartContainerRef.current) {
        setChartSize({
          width: chartContainerRef.current.clientWidth - 10, // Subtract 10px for safety
          height: 500
        });
      }
    };

    window.addEventListener('resize', updateSize);
    updateSize();

    return () => window.removeEventListener('resize', updateSize);
  }, []);

  useEffect(() => {
    console.log('Rendering IntradayChart with data:', data);
    if (!gap || !data || data.length === 0 || chartSize.width === 0) return;

    const chart = createChart(chartContainerRef.current, {
      width: chartSize.width,
      height: chartSize.height,
      layout: {
        background: { color: '#ffffff' },
        textColor: '#333',
      },
      grid: {
        vertLines: { color: '#f0f0f0' },
        horzLines: { color: '#f0f0f0' },
      },
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
        tickMarkFormatter: (time) => {
          const date = DateTime.fromSeconds(time).setZone('America/Los_Angeles');
          return date.toFormat('HH:mm');
        },
      },
    });

    const mainPane = chart.addPane(70); // 70% of the chart height for price

    const candlestickSeries = mainPane.addCandlestickSeries({
      upColor: '#26a69a',
      downColor: '#ef5350',
      borderVisible: false,
      wickUpColor: '#26a69a',
      wickDownColor: '#ef5350',
    });

    const vwapSeries = mainPane.addLineSeries({
      color: 'rgba(128, 0, 128, 0.8)', // Purple color
      lineWidth: 2,
    });

    const emaSeries = mainPane.addLineSeries({
      color: 'rgba(255, 82, 82, 0.8)',
      lineWidth: 2,
    });

    // Create the volume pane
    const volumePane = chart.addPane(30); // 30% of the chart height for volume

    const volumeSeries = volumePane.addHistogramSeries({
      color: '#26a69a',
      priceFormat: {
        type: 'volume',
      },
      priceScaleId: 'volume',
    });

    volumePane.applyOptions({
      scaleMargins: {
        top: 0.1,
        bottom: 0.2,
      },
    });

    const formattedData = data.map(item => ({
      time: DateTime.fromMillis(item.timestamp).setZone('America/Los_Angeles').toSeconds(),
      open: item.open,
      high: item.high,
      low: item.low,
      close: item.close,
      volume: item.volume,
    }));

    // Calculate VWAP
    const vwapData = calculateVWAP(formattedData);

    console.log('VWAP Data:', vwapData);

    // Calculate 10-period EMA
    const emaData = calculateEMA(formattedData, 10);

    candlestickSeries.setData(formattedData);
    vwapSeries.setData(vwapData);
    emaSeries.setData(emaData);

    // Calculate the average volume
    const avgVolume = formattedData.reduce((sum, item) => sum + item.volume, 0) / formattedData.length;

    volumeSeries.setData(formattedData.map(item => ({
      time: item.time,
      value: item.volume,
      color: item.volume > avgVolume 
        ? (item.close > item.open ? 'rgba(0, 150, 136, 0.5)' : 'rgba(255, 82, 82, 0.5)')
        : (item.close > item.open ? 'rgba(0, 150, 136, 0.2)' : 'rgba(255, 82, 82, 0.2)'),
    })));

    chart.timeScale().fitContent();

    const handleResize = () => {
      const newWidth = chartContainerRef.current.clientWidth - 10; // Subtract 10px for safety
      chart.applyOptions({ 
        width: newWidth,
        height: chartSize.height 
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      chart.remove();
    };
  }, [gap, data, chartSize]);

  // Add this function outside the useEffect hook
  const calculateVWAP = (data) => {
    let cumulativePV = 0;
    let cumulativeVolume = 0;

    return data.map((item) => {
      const typicalPrice = (item.high + item.low + item.close) / 3;
      cumulativePV += typicalPrice * item.volume;
      cumulativeVolume += item.volume;
      const vwap = cumulativePV / cumulativeVolume;

      return {
        time: item.time,
        value: vwap
      };
    });
  };

  // Add this function outside the useEffect hook
  const calculateEMA = (data, period) => {
    const k = 2 / (period + 1);
    let ema = data[0].close;

    return data.map((item, index) => {
      if (index === 0) {
        return { time: item.time, value: ema };
      }
      ema = (item.close - ema) * k + ema;
      return { time: item.time, value: ema };
    });
  };

  // Modify this function outside the useEffect hook
  const generateMarketHoursData = (data, chart) => {
    const preMarketData = [];
    const afterMarketData = [];

    // Get the price range of the entire dataset
    const priceRange = data.reduce((range, item) => {
      range.min = Math.min(range.min, item.low);
      range.max = Math.max(range.max, item.high);
      return range;
    }, { min: Infinity, max: -Infinity });

    // Add a small buffer to ensure full coverage
    const buffer = (priceRange.max - priceRange.min) * 1;
    const yMin = priceRange.min - buffer;
    const yMax = priceRange.max + buffer;

    data.forEach((item) => {
      const date = DateTime.fromSeconds(item.time).setZone('America/Los_Angeles');
      const hour = date.hour;
      const minute = date.minute;

      if (hour < 6 || (hour === 6 && minute < 30)) {
        preMarketData.push({ time: item.time, value: yMin });
        preMarketData.push({ time: item.time, value: yMax });
      } else if (hour >= 13) {
        afterMarketData.push({ time: item.time, value: yMin });
        afterMarketData.push({ time: item.time, value: yMax });
      }
    });

    return { preMarketData, afterMarketData };
  };

  return <div ref={chartContainerRef} className="ChartContainer" />;
}

export default IntradayChart;
